jQuery(document).ready(function($){	var formlabel = 0;	var old_send_to_editor = window.send_to_editor;	var old_tb_remove = window.tb_remove;
	$('.tk_fileuploader').click(function(){		formlabel = jQuery(this).parent();		tb_show('', 'media-upload.php?type=image&TB_iframe=true');				// Workaround for problems with thickbox and tabs			$('#TB_window,#TB_overlay,#TB_HideSelect').one("unload",killthickbox);						function killthickbox(e) {			e.stopPropagation();			e.stopImmediatePropagation();			return false;		}	});		window.send_to_editor = function(h) {		var ed, mce = typeof(tinymce) != 'undefined', qt = typeof(QTags) != 'undefined';		if ( !wpActiveEditor ) {			if ( mce && tinymce.activeEditor ) {				ed = tinymce.activeEditor;				wpActiveEditor = ed.id;			} else if ( !qt ) {				// return false;			}		} else if ( mce ) {			if ( tinymce.activeEditor && (tinymce.activeEditor.id == 'mce_fullscreen' || tinymce.activeEditor.id == 'wp_mce_fullscreen') )				ed = tinymce.activeEditor;			else				ed = tinymce.get(wpActiveEditor);		}			if ( ed && !ed.isHidden() ) {			// restore caret position on IE			if ( tinymce.isIE && ed.windowManager.insertimagebookmark )				ed.selection.moveToBookmark(ed.windowManager.insertimagebookmark);				if ( h.indexOf('[caption') === 0 ) {				if ( ed.plugins.wpeditimage )					h = ed.plugins.wpeditimage._do_shcode(h);			} else if ( h.indexOf('[gallery') === 0 ) {				if ( ed.plugins.wpgallery )					h = ed.plugins.wpgallery._do_gallery(h);			} else if ( h.indexOf('[embed') === 0 ) {				if ( ed.plugins.wordpress )					h = ed.plugins.wordpress._setEmbed(h);			}				ed.execCommand('mceInsertContent', false, h);		} else if ( qt ) {			QTags.insertContent(h);		} else {			if( wpActiveEditor ){				document.getElementById(wpActiveEditor).value += h;			}		}			if( formlabel ){			imgurl = $( 'img', h ).attr( 'src' );			elementurl = $( h ).attr( 'href' );						if( imgurl == undefined ){				url = elementurl;			}else{				url = imgurl;				formlabel.find( 'img' ).attr({ 					src: imgurl				});  			}			formlabel.find( 'input[type=text]' ).val( url );		}					try{tb_remove();}catch(e){};	}});// Whats up here