<?phpclass TK_WP_Form_Button extends TK_Form_Button{	/**	 * PHP 4 constructor	 *	 * @package Themekraft Framework	 * @since 0.1.0	 * 	 * @param array $args Array of [ $id Id, $name Name, $value Value, $submit use submit, $extra Extra checkbox code   ]	 */	function tk_wp_form_button( $value, $args = array() ){		$this->__construct( $value, $args );	}		/**	 * PHP 5 constructor	 *	 * @package Themekraft Framework	 * @since 0.1.0	 * 	 * @param array $args Array of [ $id Id, $name Name, $value Value, $submit use submit, $extra Extra checkbox code   ]	 */	function __construct( $value, $args = array() ){		global $tk_form_instance_option_group;				parent::__construct( $value, $args );				// Rewriting Fieldname and Input Field String for WP Savings		$name = tk_get_field_name( $value, array( 'option_group' => $tk_form_instance_option_group, 'multi_index' => $multi_index ) );		$this->str_name = ' name="' . $name . '"';	}}function tk_form_button( $value, $args = array(), $return = 'echo' ){	$button = new TK_WP_Form_Button( $value, $args );	tk_add_text_string( $value );	return tk_element_return( $button, $return );}
